        imgUrl: customizationImg,
        title: "Customization",
        desc: "Tailoring experiences to match your unique style. Where every detail is crafted just for you. Elevate your moments with our unparalleled customization services. âœ¨ðŸŽ¨ ",
    },
    
]

const ServiceList = () => {
  return (
  <>
     {servicesData.map((item,index)=> (
       <Col lg='3' key={index}>
          <ServiceCard item={item}/>
       </Col>
      ))}
  </>
  );
};

export default ServiceList
